definition.script:
node{
    sh "mkdir -p output"
    writeFile file: "output/somefile", text: "Hello World!"
    sh 'base64 /dev/urandom | head -c 10000000000 > output/testFile.txt'
    echo '####  TEST1 ####'
    sh 'date +%s'
    stash name: "first-stash", includes: "output/*"
}
node{
    unstash "first-stash"
    sh 'date +%s'
    echo '####  ####  ####'
}
